    public void validar_ingreso() {
        String usuario = jTextField1.getText();
        String clave = String.valueOf(jPasswordField1.getPassword());        

        try (Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/ic16jpm", "root", "root")) {
            String SSQL = "SELECT * FROM s_admin WHERE usuario='" + usuario + "' AND clave='" + clave + "'";
            Statement stmt = con.createStatement();
            ResultSet rs = stmt.executeQuery(SSQL);

            if (!rs.next()) {
                System.out.println("Error");
            } else {
                System.out.println("Ingresaste");                                
            }
        } catch (Exception e) {
            String error = e.toString();
            System.out.println(error);
        }
    }

